for(var tabContainers=document.getElementsByClassName("tabs"),i=0;i<tabContainers.length;i++)for(var container=tabContainers[i],tabs=container.getElementsByClassName("tab"),j=0;j<tabs.length;j++){var tab=tabs[j];tab.addEventListener("click",function(t){t.preventDefault();for(var e=this.parentElement.getElementsByClassName("tab"),a=0;a<e.length;a++)e[a].classList.remove("active");this.classList.add("active"),tabContents=this.parentElement.parentElement.getElementsByClassName("tab-content");for(a=0;a<tabContents.length;a++)tabContents[a].classList.add("hide");document.getElementById(this.getAttribute("data-tab-id")).classList.remove("hide")})}
